import "./index.css";

import { Todo } from "../../types";
import { ChangeEvent, useState } from "react";

interface ItemProps {
  todo: Todo;
  onComplete: (e: ChangeEvent<HTMLInputElement>) => void;
}

export default function Item({ todo, onComplete }: ItemProps) {
  const [todoC, setTodoC] = useState<Todo>(todo);

  let { name, id, done } = todo;
  function handleTask() {
    done = !done;
  }
  return (
    <li>
      <label>
        <input type="checkbox" checked={done} onChange={handleTask} />
        <span>{name}</span>
      </label>
      <button className="btn btn-danger" style={{ display: "none" }}>
        Delete
      </button>
    </li>
  );
}
