import "./index.css";

import { Todo } from "../../types";
import { ChangeEvent, useState } from "react";

interface ItemProps {
  todo: Todo;
  onComplete: (e: ChangeEvent<HTMLInputElement>) => void;
}

export default function Item({ todo, onComplete }: ItemProps) {
  const [todoC, setTodoC] = useState<Todo>(todo);
  const [onMouseHover, setOnMouseHover] = useState(false);

  function handleTask() {
    setTodoC({ ...todoC, done: !todoC.done });
  }

  return (
    <li>
      <label>
        <input type="checkbox" checked={todoC.done} onChange={handleTask} />
        <span>{todoC.name}</span>
      </label>
      <button className="btn btn-danger" style={{ display: "inline" }}>
        Delete
      </button>
    </li>
  );
}
