import "./index.css";

import { Todo } from "../../types";
import { useState } from "react";

interface FooterProps {
  calculateTotal: () => number;
  calculateFinished: () => number;
}

export default function Footer({
  calculateFinished,
  calculateTotal,
}: FooterProps) {
  const [total, useTotal] = useState(calculateTotal());
  const [finished, useFinished] = useState(calculateFinished());

  return (
    <div className="todo-footer">
      <label>
        <input type="checkbox" checked={finished === total} />
      </label>
      <span>
        {" "}
        <span>Finished {finished}</span> / Total: {total}
      </span>
      <button className="btn btn-danger">Delete Finished Tasks</button>
    </div>
  );
}
